.newslist {
	overflow: hidden;
	margin: 0 auto;
	width: 92%;
	padding-bottom: 20px;
}

.head-title {
	float: left;
	color: #999;
	font-size: 12px
}

#content {
	clear: both;
	font-size: 18px;
	line-height: 28px;
	overflow: hidden;
	text-align: justify;
}

#content img {
	width: 100%;
	height: auto;
	margin-top: 20px;
}

.title {
	margin-top: 16px;
	margin-bottom: 12px;
	color: #3a3a3a;
	font-weight: 700;
	font-size: 22px;
	line-height: 28px
}

.like {
	color: #505050;
	font-size: 16px;
	width: 92%;
	margin: 0px auto;
}

#tj {
	color: #505050;
	font-size: 16px
}

.item {
	width: 92%;
	overflow: hidden;
	margin: 0 auto;
	position: relative;
	display: block;
	border-bottom: 1px solid #eeeeee;
	padding: 12px 0;
}

.ex1-left {
	width: 63%;
	float: left;
	display: flex;
	flex-wrap: wrap;
	align-content: space-between
}

.categoryName {
	color: #f94d17;
	border: 1px solid #f94d17;
	font-size: 10px;
	text-align: center;
	display: inline-block;
	padding: 0px 4px;
}

.ex1-img {
	overflow: hidden;
	width: 34%;
	height: 21.3vw;
	float: right;

	.ex-img {
		min-height: 21.3vw;
		background-size: 64px;
		background-color: #F5F5F5;
		display: block;
		width: 100%;
	}
}

.ex-title {
	font-size: 15px;
}

.ex1 {
	.ex-title {
		display: -webkit-box;
		overflow: hidden;
		width: 100%;
		text-overflow: ellipsis;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical
	}
}

.ex2 {
	.ex-title {
		padding-bottom: 7px;
		display: block;
		position: relative;

	}

}

.ex2-img {
	height: 21.3vw;
	overflow: hidden;
	display: flex;
	justify-content: space-between; 
	.ex-img {
		display: block;
		width: 32%;
		min-height: 100%;
	}
}
