.loadTxcss {
	padding-top: 95px;
}

.loadingText {
	font-size: 15px;
	text-align: center;
	line-height: 30px;
}

.alert {
	position: fixed;
	width: 100%;
	height: 100%;
	background: rgba(0, 0, 0, 0.6);
	z-index: 6;
	left: 0;
	top: 0;
	margin: auto;

	img {
		width: 200px;
		position: fixed;
		height: 150px;
		z-index: 7;
		left: 0;
		top: 0;
		margin: auto;
		right: 0;
		bottom: 0
	}
}

.loading {
	background: url(http://res.hodanet.com/upload/sy/20201117/17/1608196908925.png);
	width: 90%;
	height: 45px;
	background-size: 100% 100%;
	margin: 10px 5% 10px 5%;
	position: relative;

	.index {
		width: 0%;
		position: absolute;
		height: 17px;
		top: 14px;
		background: #2880e8;
		left: 1%;
		border-radius: 20px 0px 0px 20px;
		transition: 5s linear;
	}

	.redBag {
		width: 38px;
		position: absolute;
		right: 0;
		bottom: 5px
	}

	.gif {
		bottom: 50px;
		width: 40px;
		position: absolute;
		right: -10px;
		animation: gif 1s infinite;
	}

}

@keyframes gif {
	0 {
		bottom: 50px
	}

	50% {
		bottom: 55px;
	}

	100% {
		bottom: 50px
	}
}

h3 {
	margin: 40px 0 0;
}

ul {
	list-style-type: none;
	padding: 0;
	margin: 0;
}

li {
	display: inline-block;
	margin: 0 10px;
}

a {
	color: #000000;
}

.content {
	margin: 0 auto;
	width: 100%;
	overflow: hidden;
}

img {
	width: 100%
}

.step2 {
	color: #fb131e;
	animation: big 1s infinite;
}

@keyframes big {
	0 {

		transform: scale(1);
	}

	50% {

		transform: scale(1.2);
	}

	100% {

		transform: scale(1);
	}
}

.newscontent {
	overflow: hidden;
	margin: 0 auto;
	width: 92%;
}

.newslist {
	overflow: hidden;
	margin: 0 auto;
	width: 92%;
}

.head-title {
	float: left;
	color: #999;
	font-size: 12px
}

#content {
	clear: both;
	font-size: 18px;
	line-height: 28px;
	overflow: hidden;
	text-align: justify;
}

#content img {
	width: 100%;
	height: auto;
	margin-top: 20px;
}

.title {
	margin-top: 16px;
	margin-bottom: 12px;
	color: #3a3a3a;
	font-weight: 700;
	font-size: 22px;
	line-height: 28px
}

.like {
	color: #505050;
	font-size: 16px;
	width: 92%;
	margin: 0px auto;
}

#tj {
	color: #505050;
	font-size: 16px
}

.item {
	width: 92%;
	overflow: hidden;
	margin: 0 auto;
	position: relative;
	display: block;
	border-bottom: 1px solid #eeeeee;
	padding: 12px 0;
}

.ex1-left {
	width: 63%;
	float: left;
	display: flex;
	flex-wrap: wrap;
	align-content: space-between
}

.categoryName {
	color: #f94d17;
	border: 1px solid #f94d17;
	font-size: 10px;
	text-align: center;
	display: inline-block;
	padding: 0px 4px;
}

.ex1-img {
	overflow: hidden;
	width: 34%;
	height: 21.3vw;
	float: right;

	.ex-img {
		min-height: 21.3vw;
		background-size: 64px;
		background-color: #F5F5F5;
		display: block;
		width: 100%;
	}
}

.adHidden {
	display: none;
}

.ex-title {
	font-size: 15px;
}

.ex1 {
	.ex-title {
		display: -webkit-box;
		overflow: hidden;
		width: 100%;
		text-overflow: ellipsis;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical
	}
}

.ex2 {
	.ex-title {
		padding-bottom: 7px;
		display: block;
		position: relative;

	}

}

.ex2-img {
	height: 21.3vw;
	overflow: hidden;
	display: flex;
	justify-content: space-between;

	.ex-img {
		display: block;
		width: 32%;
		min-height: 100%;
	}
}

.Unfolded-btn {
	text-align: center;
	position: relative;
	padding-bottom: 40px;
}

.Unfolded-btn .Unfolded-cover {
	position: absolute;
	top: -78px;
	height: 78px;
	width: 100%;
	background-image: linear-gradient(-180deg, hsla(0, 0%, 100%, 0), #fff);
}


.Unfolded-btn .Unfolded-link {
	display: inline-block;
	cursor: pointer;
	font-size: 16px;
	line-height: 22px;
	border-radius: 29px;
	color: #2a90d7;
	text-align: center;
}

.Unfolded-btn .Unfolded-link:after {
	content: "";
	background-image: url(//mini.eastday.com/toutiaoh5/img/down.png);
	background-size: contain;
	background-repeat: no-repeat;
	width: 12px;
	height: 12px;
	display: inline-block;
	margin-left: 5px;
}
